mapper.select:

SELECT *
FROM ( SELECT bank_account_id, bank_account_name, bank_account_number,
			  bank_account_balance, bank_account_type, bank_name,
			  use_state, reg_date, rownum rNum
	     FROM ( select *
	     	    FROM bank_account
	     	    ORDER BY bank_account_name
	     	   )
	 )
WHERE rNum >= #{start} AND rNum <= #{end}
