FROM (SELECT P.PURCHASE_ID, P.ACCOUNT_ID, P.ORDER_ID, P.PRODUCT_ID, P.COMPANY_ID, P.EMPLOYEE_ID, P.REG_DATE, P.UPDATE_DATE, P.STORAGE_IN_DATE, P.COUNT_PURCHASE, P.SUPPLY_PRICE, P.PURCHASE_STATE, P.CONDITION_NOTE_PAYABLE, C.COMPANY_NAME
FROM PURCHASE_ORDER P , COMPANY C WHERE P.COMPANY_ID = C.COMPANY_ID ORDER BY PURCHASE_ID ) ) WHERE rNum >= #{start} AND rNUM <= #{end}
