mapper.select:
SELECT * FROM ( SELECT * FROM ( SELECT sales_id AS pro_id, storage_out_date AS sto_date, count_sales AS moving_stock, sales_state AS sto_state, product_id FROM sales_order WHERE sales_state = '22223' OR sales_state = '24621'
AND product_id in
#{product}
UNION
SELECT sales_id AS pro_id, storage_out_date AS sto_date, count_sales AS moving_stock, sales_state AS sto_state, product_id FROM sales_order WHERE sales_state = '0'
AND product_id in
#{product}
UNION
SELECT purchase_id AS pro_id, storage_in_date AS sto_date, count_purchase AS moving_stock, purchase_state AS sto_state, product_id FROM purchase_order WHERE purchase_state = '23205' OR purchase_state = '22411' OR purchase_state = '22412'
AND product_id in
#{product}
UNION
SELECT s.stock_order_id AS pro_id, s.update_date AS sto_date, a.delete_stock AS moving_stock, s.stock_state AS sto_state, s.product_id FROM stock_order s JOIN adjustment_inventory a ON s.stock_order_id = a.stock_order_id
WHERE s.product_id in
#{product}
) ORDER BY sto_date ) WHERE sto_date BETWEEN #{start_day} AND #{end_day}

SELECT p.product_name as product_name, p.product_id as product_id, s.stock_amount as stock_amount, s.warehouse_id as warehouse_id FROM product p JOIN stock_information s ON s.product_id = p.product_id
WHERE p.product_id in
#{product_id}

SELECT DISTINCT p.product_id as product_id FROM product p JOIN stock_information s ON s.product_id = p.product_id
WHERE p.product_id in
#{product_id}

update.if:

UPDATE 	STOCK_INFORMATION 
SET 	STOCK_AMOUNT = STOCK_AMOUNT - #{available_stock}
WHERE 	PRODUCT_ID = #{product_id} 
AND 	WAREHOUSE_ID = 1001

UPDATE 	STOCK_INFORMATION 
SET 	STOCK_AMOUNT = STOCK_AMOUNT + #{available_stock} 
WHERE 	PRODUCT_ID = #{product_id} 
AND 	WAREHOUSE_ID = 3001

UPDATE 	STOCK_INFORMATION 
SET 	STOCK_AMOUNT = STOCK_AMOUNT + #{count_purchase} 
WHERE 	PRODUCT_ID = #{product_id} 
AND 	WAREHOUSE_ID = 1001

UPDATE 	STOCK_INFORMATION 
SET 	STOCK_AMOUNT = STOCK_AMOUNT - #{available_stock} 
WHERE 	PRODUCT_ID = #{product_id} 
AND 	WAREHOUSE_ID = 3001


UPDATE 	STOCK_INFORMATION 
SET 	STOCK_AMOUNT = STOCK_AMOUNT - #{lack_stock}
WHERE 	PRODUCT_ID = #{product_id} 
AND 	WAREHOUSE_ID = 1001

mapper.update:
UPDATE	SALES_ORDER
SET		SALES_STATE = #{stock_state}
WHERE	SALES_ID = #{stock_order_type}
AND		ACCOUNT_ID = 500011030000

UPDATE	purchase_order
SET		purchase_state = #{stock_state}
WHERE	purchase_id = #{stock_order_type}
AND		ACCOUNT_ID = 500011050000

UPDATE	stock_order
SET		stock_state = #{stock_state}
WHERE	stock_order_type = #{stock_order_type}

mapper.select:
SELECT	stock_order_type
FROM	stock_order
WHERE	stock_order_id = #{order_id}
