mapper.update:

UPDATE	PURCHASE_ORDER
SET		PURCHASE_STATE = #{purchase_state}
WHERE	PURCHASE_ID = #{purchase_id}
AND		ACCOUNT_ID = #{account_id}


UPDATE	PURCHASE_ORDER
SET		PURCHASE_STATE = 23208
WHERE	PURCHASE_ID = #{purchase_id}

mapper.select:

		SELECT  COUNT(S.SALES_ID) 
	    FROM	PURCHASE ORDER PO



	                AND ACCOUNT_ID IN #{account}



	                AND PRODUCT_ID IN #{product}



	                AND COMPANY_ID IN #{company}



	                AND EMPLOYEE_ID IN #{employee}



	                OR 1=1





           AND PO.ACCOUNT_ID IN #{account}



           AND PO.PRODUCT_ID IN #{product}



           AND PO.COMPANY_ID IN #{company}



           AND PO.EMPLOYEE_ID IN #{employee}

SELECT * FROM ( SELECT PURCHASE_ID, ACCOUNT_ID, ACCOUNT_NAME, ORDER_ID, PRODUCT_NAME, COMPANY_ID, COMPANY_NAME, EMPLOYEE_NAME, REG_DATE, UPDATE_DATE, STORAGE_IN_DATE, COUNT_PURCHASE, SUPPLY_PRICE, CONDITION_NOTE_PAYABLE, PURCHASE_STATE, STATE_NAME ,ROWNUM rNum
FROM ( SELECT PO.PURCHASE_ID, PO.ACCOUNT_ID, NVL(( SELECT ACCOUNT_NAME FROM ACCOUNT A WHERE PO.ACCOUNT_ID = A.ACCOUNT_ID ),'입력값 오류' ) AS ACCOUNT_NAME , PO.ORDER_ID, NVL(( SELECT PRODUCT_NAME FROM PRODUCT P WHERE PO.PRODUCT_ID = P.PRODUCT_ID ),'입력값 오류' ) AS PRODUCT_NAME , PO.COMPANY_ID, NVL(( SELECT COMPANY_NAME FROM COMPANY C WHERE PO.COMPANY_ID = C.COMPANY_ID ),'입력값 오류' ) AS COMPANY_NAME , NVL(( SELECT EMPLOYEE_NAME FROM EMPLOYEE E WHERE PO.EMPLOYEE_ID = E.EMPLOYEE_ID ),'입력값 오류' ) AS EMPLOYEE_NAME , PO.REG_DATE, PO.UPDATE_DATE, PO.STORAGE_IN_DATE, PO.COUNT_PURCHASE, PO.SUPPLY_PRICE, PO.CONDITION_NOTE_PAYABLE, PO.PURCHASE_STATE, NVL(( SELECT KOR_NAME FROM STATE ST WHERE PO.PURCHASE_STATE = ST.CODE ),'입력값 오류' ) AS STATE_NAME
FROM PURCHASE_ORDER PO
AND PO.ACCOUNT_ID IN #{account}
AND PO.PRODUCT_ID IN #{product}
AND PO.COMPANY_ID IN #{company}
AND PO.EMPLOYEE_ID IN #{employee}
AND 1=1
AND PO.ACCOUNT_ID IN #{account}
AND PO.PRODUCT_ID IN #{product}
AND PO.COMPANY_ID IN #{company}
AND PO.EMPLOYEE_ID IN #{employee}
ORDER BY PURCHASE_ID DESC, ACCOUNT_ID ASC ) )
WHERE rNum BETWEEN #{start} AND #{end} ORDER BY PURCHASE_ID DESC, ACCOUNT_ID ASC
